          name="packaging_cost"
          placeholder="Custo da Embalagem"
          className="register--input"
          onChange={handleChangeValues}
        />
        <br/>
        <button class="register--btn" onClick={() => handleClickButton()}>Cadastrar</button>
      </div>
    </div>
  );
}

export default App;
